import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Slider.css"
import banner1 from "../banner1.png"

const Sliders = () => {
  // Rastgele fotoğraf URL'leri için bir dizi oluştur
  const imageUrls = [
    "https://cdn.dsmcdn.com/mnresize/-/-//ty1320/product/media/images/prod/SPM/PIM/20240506/03/009559e9-203b-3ffa-af2d-2ac155d7db07/1_org_thumb.jpg",
    banner1,
    "https://via.placeholder.com/600x400?text=Third+Slide",
    "https://via.placeholder.com/600x400?text=Fourth+Slide",
  ];

  const settings = {
    dots: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 1000,
  };

  return (
    <div>
      <Slider {...settings}>
        {/* Fotoğraf URL'lerini kullanarak slider'a ekleyin */}
        {imageUrls.map((imageUrl, index) => (
          <div key={index}>
            <img
              src={imageUrl}
              alt={`Slide ${index + 1}`}
              style={{ width: "100%", height: "300px", objectFit: "cover" }}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Sliders;
