    }
  }

  // ...existing code...
`;

const SliderContainer = styled.div`
  width: 100%;
  height: 200px; // Slider'ın yüksekliğini ayarlayın
  background-color: lightblue; // Slider'ın arka plan rengini ayarlayın
  margin-bottom: 20px; // Slider ve Nav arasında boşluk oluşturma
`;

const Header = () => {
