      <GlobalStyle />
      <SliderContainer>
        {/* Slider içeriği */}
      </SliderContainer>
      <HeaderContainer>
        {/* LogoContainer ve Nav bileşenleri */}
      </HeaderContainer>
      <SearchAndThemeContainer>
        {/* SearchContainer ve ThemeToggle bileşenleri */}
      </SearchAndThemeContainer>
    </ThemeProvider>
  );
};

export default Header;
