import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Sliders = () => {
  // Rastgele fotoğraflar için bir dizi oluştur
  const images = [
    "https://via.placeholder.com/600x400?text=First+Slide",
    "https://via.placeholder.com/600x400?text=Second+Slide",
    "https://via.placeholder.com/600x400?text=Third+Slide",
    "https://via.placeholder.com/600x400?text=Fourth+Slide",
  ];

  const settings = {
    dots: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 1000,
  };

  return (
    <div>
      <h2>Carousel Component</h2>
      <Slider {...settings}>
        {/* Fotoğrafların olduğu dizi üzerinde dönerek her bir fotoğrafı slider'a ekleyin */}
        {images.map((image, index) => (
          <div key={index} style={{ borderRadius: "10px" }}>
            <img
              src={image}
              alt={`Slide ${index + 1}`}
              style={{ width: "100%", height: "300px", objectFit: "cover" }}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Sliders;
